              console.log("title ************************");
              console.log(title);
              client.send(JSON.stringify({ type: "delete", data: title }));
            }
          }
        });
      });
    }

    ws.on("error", (error) => {
      console.error(error);
      app.locals.message = error.message;
      app.locals.error = process.env.NODE_ENV !== " production" ? error : {};
      app.render("error");
    });

    ws.on("close", () => console.log(`${req.url} connection close`));
  });
};
