mapper.select:
SELECT ranked.*, (SELECT count(*) FROM view WHERE board_idx = ranked.board_idx) AS board_count
	FROM (
	    SELECT rank() OVER (ORDER BY board_idx DESC) AS no, con.*
	    FROM (
	        SELECT * FROM board
	        WHERE board_sort = '공지사항'

	    	) AS con
	) AS ranked
	WHERE no BETWEEN #{start} AND #{end};
