mapper.select:
SELECT ranked.*, (SELECT count(*) FROM view WHERE board_idx = ranked.board_idx) AS board_count FROM ( SELECT rank() OVER (ORDER BY board_idx DESC) AS no, con.* FROM ( SELECT * FROM board WHERE board_sort = '공지사항'
AND (
user_id LIKE CONCAT('%', #{user}, '%')
OR
board_title LIKE CONCAT('%', #{title}, '%')
OR
board_content LIKE CONCAT('%', #{content}, '%')
OR
DATE_FORMAT(board_register_date, '%Y-%m-%d') = #{regdate}
)
) AS con ) AS ranked WHERE no BETWEEN #{start} AND #{end}
