mapper.select:
select ranked.*, (select count(*) from view where board_idx = ranked.board_idx) board_count from ( select rank() over(order by board_idx desc) as no, con.* from ( select * from board
user_id LIKE CONCAT('%', #{user}, '%')
board_title LIKE CONCAT('%', #{title}, '%')
board_content LIKE CONCAT('%', #{content}, '%')
DATE_FORMAT(board_register_date, '%Y-%m-%d') = #{regdate}
) as con ) as ranked where no between #{ start } and #{ end } and board_sort = '공지사항'

mapper.insert:
UPDATE board 
SET board_isSelected = 1 
WHERE board_idx=#{board_idx} AND user_id=#{user_id}
